import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function Tdashboard() {
  const [allImage, setAllImage] = useState(null);
  const [pdfFile, setPdfFile] = useState(null); // Stores the selected PDF file

  useEffect(() => {
    getPdf();
  }, []);

  const getPdf = async () => {
    try {
      const result = await axios.get(
        "http://localhost:5000/api/pdfs/get-files"
      );
      console.log(result.data.data);
      setAllImage(result.data.data);
    } catch (error) {
      console.error("Error fetching PDFs:", error);
    }
  };

  const showPdf = (fileName) => {
    window.open(`http://localhost:5000/files/${fileName}`, "_blank");
  };

  const handleDelete = async (fileName) => {
    try {
      const response = await axios.delete(
        `http://localhost:5000/api/pdfs/delete/${fileName}`
      );

      if (response.data.status === "ok") {
        alert("PDF deleted successfully!");
        getPdf(); // Refresh the list after deletion
      } else {
        alert("Error deleting PDF");
      }
    } catch (error) {
      console.error("Error deleting PDF:", error);
      alert("Failed to delete PDF");
    }
  };

  return (
    <div className="uploaded p-4">
      <Link
        to="/addnotes"
        className="text-lg font-semibold mb-4 ml-3 bg-green-500 px-5 py-2 rounded-md"
      >
        Uploaded PDF
      </Link>
      <br />
      <div className="output-div space-y-4 p-3">
        {allImage === null ? (
          <p>Loading PDFs...</p>
        ) : (
          allImage.map((data) => (
            <div
              className="inner-div border p-4 rounded-lg shadow-md bg-white transition-transform transform hover:scale-105"
              key={data._id}
            >
              <h6 className="font-semibold text-lg text-gray-800 mb-2">
                üìÑ {data.name}
              </h6>
              <div className="flex gap-3">
                <button
                  className="bg-blue-600 text-white px-5 py-2 rounded-lg shadow-md hover:bg-blue-700 transition duration-300"
                  onClick={() => showPdf(data.file)}
                >
                  üìÇ Show PDF
                </button>
                <button
                  className="bg-red-500 text-white px-5 py-2 rounded-lg shadow-md hover:bg-red-600 transition duration-300"
                  onClick={() => handleDelete(data.file)}
                >
                  üóëÔ∏è Delete PDF
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* ‚úÖ Show PDF Preview when a file is selected */}
      {pdfFile && (
        <div className="mt-6">
          <h4 className="text-lg font-semibold">PDF Preview:</h4>
          <iframe
            src={pdfFile}
            width="100%"
            height="500px"
            className="border rounded"
          ></iframe>
        </div>
      )}
    </div>
  );
}

export default Tdashboard;
