import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import Swal from "sweetalert2";

function Sdashboard() {
  const [marks, setMarks] = useState({
    s1: "",
    s2: "",
    s3: "",
    s4: "",
    s5: "",
    s6: "",
    s7: "",
    s8: "",
    s9: "",
  });
  const [image, setImage] = useState("");
  const navigate = useNavigate();

  // Function to ensure input is between 0 and 100
  const handleNumberChange = (e) => {
    const { name, value } = e.target;
    let numValue = Number(value);

    if (numValue < 0) numValue = 0;
    if (numValue > 100) numValue = 100;

    setMarks((prev) => ({ ...prev, [name]: numValue }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/api/marks", { ...marks, image })
      .then(() => {
        Swal.fire(
          "Congratulations! You Have Successfully updated the marks ðŸ˜Š",
          "",
          "success"
        );
        navigate("/books");
      })
      .catch(() => {
        alert("Failed");
      });
  };

  return (
    <div className="w-full relative mt-0 shadow-2xl rounded my-24 overflow-hidden">
      <div className="top h-64 w-full bg-blue-600 overflow-hidden relative">
        <img
          src="https://images.unsplash.com/photo-1503264116251-35a269479413?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1050&q=80"
          alt=""
          className="bg w-full h-full object-cover object-center absolute z-0"
        />
        <div className="flex flex-col justify-center items-center relative h-full bg-black bg-opacity-50 text-white">
          <img
            src="https://images.unsplash.com/photo-1438761681033-6461ffad8d80?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1050&q=80"
            className="h-24 w-24 object-cover rounded-full"
            alt="Profile"
            name="image"
            onChange={(e) => setImage(e.target.value)}
          />
          <h1 className="text-2xl font-semibold">Student</h1>
          <h4 className="text-sm font-semibold">Joined Since '19</h4>
        </div>
      </div>

      <div className="grid grid-cols-12 bg-white">
        <div className="col-span-12 md:border-solid md:border-l md:border-black md:border-opacity-25 h-full pb-12 md:col-span-10">
          <div className="px-4 pt-4">
            <form onSubmit={handleSubmit} className="flex flex-col space-y-8">
              <h3 className="text-2xl font-semibold p-6">Basic Information</h3>
              <hr />

              {/* Input Fields */}
              {[
                ["Maths", "s1"],
                ["Science", "s2"],
                ["English", "s3"],
                ["Maths", "s4"],
                ["Science", "s5"],
                ["English", "s6"],
                ["Maths", "s7"],
                ["Science", "s8"],
                ["English", "s9"],
              ].map(([label, name]) => (
                <div key={name} className="form-item w-full">
                  <label className="text-xl">{label}</label>
                  <input
                    type="number"
                    name={name}
                    value={marks[name]}
                    required
                    onChange={handleNumberChange}
                    className="w-full appearance-none text-black text-opacity-50 rounded shadow py-1 px-2 focus:outline-none focus:shadow-outline focus:border-blue-200"
                  />
                </div>
              ))}

              <button className="bg-green-600 text-white font-semibold w-32 py-2 px-4 rounded-md shadow-md hover:bg-green-700 hover:shadow-lg transition-all duration-300 ease-in-out transform hover:scale-105">
                Submit
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Sdashboard;
