import { useState, useEffect } from "react";

const Dashboard = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [isNotificationsOpen, setIsNotificationsOpen] = useState(false);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [activeMenu, setActiveMenu] = useState(null);
  const [selectedColor, setSelectedColor] = useState("cyan");

  // Dark mode initialization
  useEffect(() => {
    const savedDarkMode = localStorage.getItem("dark") === "true";
    setIsDarkMode(savedDarkMode);
  }, []);

  // Color theme setup
  useEffect(() => {
    const root = document.documentElement;
    root.style.setProperty("--color-primary", `var(--color-${selectedColor})`);
    root.style.setProperty(
      "--color-primary-50",
      `var(--color-${selectedColor}-50)`
    );
    root.style.setProperty(
      "--color-primary-100",
      `var(--color-${selectedColor}-100)`
    );
    root.style.setProperty(
      "--color-primary-light",
      `var(--color-${selectedColor}-light)`
    );
    root.style.setProperty(
      "--color-primary-lighter",
      `var(--color-${selectedColor}-lighter)`
    );
    root.style.setProperty(
      "--color-primary-dark",
      `var(--color-${selectedColor}-dark)`
    );
    root.style.setProperty(
      "--color-primary-darker",
      `var(--color-${selectedColor}-darker)`
    );
    localStorage.setItem("color", selectedColor);
  }, [selectedColor]);

  const toggleMenu = (menu) => {
    setActiveMenu(activeMenu === menu ? null : menu);
  };

  return (
    <div className={`min-h-screen ${isDarkMode ? "dark" : ""}`}>
      <div className="flex h-screen antialiased text-gray-900 bg-gray-100 dark:bg-darker dark:text-light">
        {/* Sidebar */}
        <aside
          className={`flex-shrink-0 w-64 bg-white border-r dark:border-primary-darker dark:bg-darker ${
            isMobileMenuOpen ? "block" : "hidden md:block"
          }`}
        >
          <div className="flex flex-col h-full">
            <nav className="flex-1 px-2 py-4 space-y-2 overflow-y-hidden hover:overflow-y-auto">
              {/* Dashboard Menu */}
              <div>
                <button
                  onClick={() => toggleMenu("dashboard")}
                  className="flex items-center w-full p-2 text-gray-500 transition-colors rounded-md dark:text-light hover:bg-primary-100 dark:hover:bg-primary"
                >
                  <svg
                    className="w-5 h-5"
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth="2"
                      d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6"
                    />
                  </svg>
                  <span className="ml-2 text-sm">Dashboards</span>
                </button>
                {activeMenu === "dashboard" && (
                  <div className="mt-2 space-y-2 px-7">
                    <a
                      href="/"
                      className="block p-2 text-sm text-gray-700 dark:text-light"
                    >
                      Default
                    </a>
                  </div>
                )}
              </div>

              {/* Other menu items... */}
            </nav>
          </div>
        </aside>

        {/* Main Content */}
        <div className="flex-1 h-full overflow-x-hidden overflow-y-auto">
          {/* Header */}
          <header className="bg-white dark:bg-darker">
            <div className="flex items-center justify-between p-2 border-b dark:border-primary-darker">
              <button
                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                className="p-1 md:hidden text-primary-lighter bg-primary-50 hover:text-primary hover:bg-primary-100 dark:hover:text-light dark:hover:bg-primary-dark"
              >
                <svg
                  className="w-8 h-8"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M4 6h16M4 12h16M4 18h16"
                  />
                </svg>
              </button>

              <h1 className="text-2xl font-bold tracking-wider uppercase text-primary-dark dark:text-light">
                K-WD
              </h1>

              <div className="flex items-center space-x-2">
                <button
                  onClick={() => setIsDarkMode(!isDarkMode)}
                  className="p-2 transition-colors duration-200 rounded-full hover:bg-primary-100 dark:hover:bg-primary-dark"
                >
                  {isDarkMode ? "üåû" : "üåô"}
                </button>
                <button
                  onClick={() => setIsNotificationsOpen(true)}
                  className="p-2 transition-colors duration-200 rounded-full hover:bg-primary-100 dark:hover:bg-primary-dark"
                >
                  üîî
                </button>
                <button
                  onClick={() => setIsSearchOpen(true)}
                  className="p-2 transition-colors duration-200 rounded-full hover:bg-primary-100 dark:hover:bg-primary-dark"
                >
                  üîç
                </button>
              </div>
            </div>
          </header>

          {/* Dashboard Content */}
          <main>
            <div className="p-4">
              <div className="grid grid-cols-1 gap-8 md:grid-cols-2 lg:grid-cols-4">
                {/* Stats Cards */}
                <div className="p-4 bg-white rounded-md dark:bg-darker">
                  <h3 className="text-gray-500 dark:text-primary-light">
                    Total Users
                  </h3>
                  <p className="text-2xl font-semibold">50,021</p>
                </div>
                {/* Add more cards... */}
              </div>

              {/* Charts Section */}
              <div className="grid grid-cols-1 gap-8 mt-8 lg:grid-cols-3">
                <div className="col-span-2 p-4 bg-white rounded-md dark:bg-darker">
                  <h4 className="text-lg font-semibold text-gray-500 dark:text-light">
                    Bar Chart
                  </h4>
                  <div className="h-72">{/* Chart implementation */}</div>
                </div>
                {/* Add more charts... */}
              </div>
            </div>
          </main>
        </div>

        {/* Settings Panel */}
        {isSettingsOpen && (
          <div className="fixed inset-0 z-10 bg-black bg-opacity-50">
            <div className="fixed inset-y-0 right-0 z-20 w-full max-w-xs bg-white dark:bg-darker">
              {/* Settings content... */}
              <button onClick={() => setIsSettingsOpen(false)} className="p-2">
                ‚úï
              </button>
            </div>
          </div>
        )}

        {/* Notifications Panel */}
        {isNotificationsOpen && (
          <div className="fixed inset-0 z-10 bg-black bg-opacity-50">
            <div className="fixed inset-y-0 right-0 z-20 w-full max-w-xs bg-white dark:bg-darker">
              {/* Notifications content... */}
              <button
                onClick={() => setIsNotificationsOpen(false)}
                className="p-2"
              >
                ‚úï
              </button>
            </div>
          </div>
        )}

        {/* Search Panel */}
        {isSearchOpen && (
          <div className="fixed inset-0 z-10 bg-black bg-opacity-50">
            <div className="fixed inset-y-0 right-0 z-20 w-full max-w-xs bg-white dark:bg-darker">
              {/* Search content... */}
              <button onClick={() => setIsSearchOpen(false)} className="p-2">
                ‚úï
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
